mapper.select:
SELECT COUNT(*)
  FROM MEMBERINFO 
 WHERE PHONE = #{phone}

SELECT Password,
       Name
  FROM MasterInfo 
 WHERE PHONE = #{cellPhone}
   AND NAME = #{name}

mapper.update:
UPDATE MasterInfo
   SET PASSWORD = #{newPassword}
 WHERE PHONE = #{cellPhone}
   AND MCODE = #{mcode}

mapper.delete:
DELETE
  FROM MasterInfo
 WHERE PHONE = #{cellPhone}
   AND NAME = #{name}
   AND PASSWORD = #{password}
